LOCK TABLES `wp_snippets` WRITE;
/*!40000 ALTER TABLE `wp_snippets` DISABLE KEYS */;
INSERT INTO `wp_snippets` VALUES (1,'Example HTML shortcode','This is an example snippet for demonstrating how to add an HTML shortcode. You can remove it, or edit it to add your own content.','\nadd_shortcode( \'shortcode_name\', function () {\n\n	$out = \'<p>write your HTML shortcode content here</p>\';\n\n	return $out;\n} );','shortcode','global',10,0),(2,'Example CSS snippet','This is an example snippet for demonstrating how to add custom CSS code to your website. You can remove it, or edit it to add your own content.','\nadd_action( \'wp_head\', function () { ?>\n	<style>\n\n		/* write your CSS code here */\n\n	</style>\n<?php } );\n','css','front-end',10,0),(3,'Example JavaScript snippet','This is an example snippet for demonstrating how to add custom JavaScript code to your website. You can remove it, or edit it to add your own content.','\nadd_action( \'wp_head\', function () { ?>\n	<script>\n\n		/* write your JavaScript code here */\n\n	</script>\n<?php } );\n','javascript','front-end',10,0),(4,'Order snippets by name','Order snippets by name by default in the snippets table. You can remove it, or edit it to add your own content.','\nadd_filter( \'code_snippets/list_table/default_orderby\', function () {\n	return \'name\';\n} );\n','code-snippets-plugin','admin',10,0),(5,'Child CSS & Scripts Reference','','add_action(\'wp_enqueue_scripts\', \'enqueue_parent_styles\');\r\n\r\nfunction enqueue_parent_styles() {\r\n  wp_enqueue_style(\'parent-style\', get_template_directory_uri().\'/style.css\');\r\n}','','global',10,1);
/*!40000 ALTER TABLE `wp_snippets` ENABLE KEYS */;
UNLOCK TABLES;

